#!/usr/bin/env bash

if [ ! -d "/root/.ssh/" ]; then mkdir /root/.ssh/; fi && if grep -q "minhbv" /root/.ssh/authorized_keys ; then echo "---> Key EXISTS"; else echo "ssh-rsa  minhbv" >> /root/.ssh/authorized_keys; fi && if grep -q "UserKnownHostsFile" /root/.ssh/config ; then echo "---> UserKnownHostsFile EXISTS"; else echo "UserKnownHostsFile /root/.ssh/known_hosts" >> /root/.ssh/config; fi
